import './App.css';
import Navigation from "./Components/Navigation/Navigation";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import Rank from "./Components/Rank/Rank";
import FaceRecognition from "./Components/FaceRecognition/FaceRecognition";
import ParticlesBg from 'particles-bg';
import Clarifai from "clarifai";
import { useState } from 'react'; // Importing useState hook

// Remove the Clarifai App instance if you're not using it
// const app = new Clarifai.App({
// apiKey: "3635be799be94ec19a8434b00959ec43" // Ensure this is a valid key
// });

// Your PAT (Personal Access Token) can be found in the Account's Security section
const returnClarifaiRequestOptions = (imageUrl) => {
  const PAT = 'dcb52ced76444b13be68163102a8b7fc';
  const USER_ID = 'cu9ym2eezj7bb';
  const APP_ID = 'Face-Recognition-Brain';
  // // Change these to whatever model and image URL you want to use
  const MODEL_ID = 'face-detection';


  const raw = JSON.stringify({
    "user_app_id": {
        "user_id": USER_ID,
        "app_id": APP_ID
    },
    "inputs": [
        {
            "data": {
                "image": {
                    "url": imageUrl
                    // "base64": IMAGE_BYTES_STRING
                }
            }
        }
    ]
});

return {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Authorization': 'dcb52ced76444b13be68163102a8b7fc' + PAT
   },
   body: raw
  };
}


function App() {
   // State to manage the input image URL and image recognition results
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState(""); // State to store the image URL for recognition
  const [box, setBox] = useState({}); // State to store bounding box for face detection

  // Event handler for input change
  const onInputChange = (event) => {
    setInput(event.target.value); // Update state (if needed)
  };

  // This function handles the button click and API request
  const onButtonSubmit = () => {
    console.log("Input URL:", input);
    setImageUrl(input); // Update the imageUrl state


// Clarifai API call  https://api.clarifai.com/v2/models/face-detection/outputs

  fetch("https://api.clarifai.com/v2/models/" + "face-detection" + "/outputs", returnClarifaiRequestOptions(input))
    .then(response => response.json())
    .then(data => {
      if (data.outputs && data.outputs[0].data.regions) {
        // Get the bounding box details from the API response
          const clarifaiFaces = data.outputs[0].data.regions.map(region => {
            return region.region_info.bounding_box;
          });
          clarifaiFaces.foreach((face, index) => {
            displayFaceBox(face, index);
          });
        }
    })
    .catch(err => console.log(err)); // Catch any errors
};

// Function to calculate the bounding box
const displayFaceBox = (box) => {
  setBox(box); // Set the bounding box to state
};

  return (
    <div className="App">
    <ParticlesBg className="particles"
        type="cobweb"
        color="#ffffff"
        num={300}
        bg={true}
      ></ParticlesBg>
    <Navigation/>
    <Logo/>
    <Rank/>
    <ImageLinkForm 
    onInputChange={onInputChange} 
    onButtonSubmit={onButtonSubmit} 
    />
    <FaceRecognition imageUrl={imageUrl} box={box} />
    </div>
  );
}

export default App;
