import './App.css';
import Navigation from "./Components/Navigation/Navigation";
import Signin from "./Components/Signin/Signin";
import Register from "./Components/Register/Register";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import Rank from "./Components/Rank/Rank";
import FaceRecognition from "./Components/FaceRecognition/FaceRecognition";
import ParticlesBg from 'particles-bg';
import Clarifai from "clarifai";
import { useState } from 'react'; // Importing useState hook

// Clarifai API setup remains the same...
const returnClarifaiRequestOptions = (imageUrl) => {
  const PAT = 'dcb52ced76444b13be68163102a8b7fc'; // Correctly declare the PAT
  const USER_ID = 'cu9ym2eezj7bb'; // Your Clarifai User ID
  const APP_ID = 'Face-Recognition-Brain'; // Your Clarifai App ID
  const MODEL_ID = 'face-detection'; // The model you're using

  const raw = JSON.stringify({
    "user_app_id": {
      "user_id": USER_ID,
      "app_id": APP_ID
    },
    "inputs": [
      {
        "data": {
          "image": {
            "url": imageUrl // The image URL to analyze
          }
        }
      }
    ]
  });

  return {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Authorization': 'Key ' + PAT // Correctly use the PAT here
    },
    body: raw
  };
};

function App() {
   // State to manage the input image URL, image recognition results, and route
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState(""); // State to store the image URL for recognition
  const [box, setBox] = useState({}); // State to store bounding box for face detection
  const [route, setRoute] = useState("Signin"); // State to manage the current route

  // Event handler for input change
  const onInputChange = (event) => {
    setInput(event.target.value); // Update state (if needed)
  };

  // This function handles the button click and API request
  const onButtonSubmit = () => {
    console.log("Input URL:", input);
    setImageUrl(input); // Update the imageUrl state

    // Clarifai API call
    fetch("https://api.clarifai.com/v2/models/" + "face-detection" + "/outputs", returnClarifaiRequestOptions(input))
      .then(response => response.json())
      .then(data => {
        if (data.outputs && data.outputs[0].data.regions) {
          const clarifaiFaces = data.outputs[0].data.regions.map(region => {
            return region.region_info.bounding_box;
          });
          clarifaiFaces.forEach((face, index) => {
            displayFaceBox(face, index);
          });
        }
      })
      .catch(err => console.log(err)); // Catch any errors
  };

  // Function to calculate the bounding box
  const displayFaceBox = (box) => {
    setBox(box); // Set the bounding box to state
  };

  // Function to change the route (e.g., from Signin to Home)
  const changeRoute = (route) => {
    setRoute(route); // Update the route state
  };

  return (
    <div className="App">
      <ParticlesBg className="particles"
        type="cobweb"
        color="#ffffff"
        num={300}
        bg={true}
      ></ParticlesBg>
      <Navigation changeRoute={changeRoute} />
      {route === "home"  
        ? <div>
            <Logo />
            <Rank />
            <ImageLinkForm 
              onInputChange={onInputChange} 
              onButtonSubmit={onButtonSubmit} 
            />
            <FaceRecognition imageUrl={imageUrl} box={box} />
          </div>
          : (
            route === "Signin"
            ? <Signin changeRoute={changeRoute}/>
            : <Register changeRoute={changeRoute}/>
            )
      }
    </div>
  );
}

export default App;
