import './App.css';
import Navigation from "./Components/Navigation/Navigation";
import Signin from "./Components/Signin/Signin";
import Register from "./Components/Register/Register";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import Rank from "./Components/Rank/Rank";
import FaceRecognition from "./Components/FaceRecognition/FaceRecognition";
import ParticlesBg from 'particles-bg';
import Clarifai from "clarifai";
import { useState } from 'react';

function App() {
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [box, setBox] = useState({});
  const [route, setRoute] = useState("Signin"); // Default is 'Signin'
  const [isSignedIn, setIsSignedIn] = useState(false);

  // Handle input change
  const onInputChange = (event) => {
    setInput(event.target.value);
  };

  // Handle button submit
  const onButtonSubmit = () => {
    console.log("Input URL:", input);
    setImageUrl(input); 

    // Clarifai API call
    fetch("https://api.clarifai.com/v2/models/" + "face-detection" + "/outputs", returnClarifaiRequestOptions(input))
      .then(response => response.json())
      .then(data => {
        if (data.outputs && data.outputs[0].data.regions) {
          const clarifaiFaces = data.outputs[0].data.regions.map(region => {
            return region.region_info.bounding_box;
          });
          clarifaiFaces.forEach((face, index) => {
            displayFaceBox(face, index);
          });
        }
      })
      .catch(err => console.log(err));
  };

  const displayFaceBox = (face, index) => {
    setBox(prevBoxes => [...prevBoxes, face]);
  };

  const changeRoute = (route) => {
    if (route === "signout") {
      setIsSignedIn(false);
      setRoute("Signin"); // Redirect to Signin page when signed out
    } else if (route === "home") {
      setIsSignedIn(true);
    }
    setRoute(route);
  };

  return (
    <div className="App">
      <ParticlesBg className="particles" type="cobweb" color="#ffffff" num={300} bg={true} />
      <Navigation isSignedIn={isSignedIn} changeRoute={changeRoute} />
      {route === "home" 
        ? <div>
            <Logo />
            <Rank />
            <ImageLinkForm onInputChange={onInputChange} onButtonSubmit={onButtonSubmit} />
            <FaceRecognition imageUrl={imageUrl} box={box} />
          </div>
        : (
          route === "Signin"
          ? <Signin changeRoute={changeRoute} />
          : <Register changeRoute={changeRoute} />
        )
      }
    </div>
  );
}

export default App;
