import './App.css';
import Navigation from "./Components/Navigation/Navigation";
import Signin from "./Components/Signin/Signin";
import Register from "./Components/Register/Register";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import Rank from "./Components/Rank/Rank";
import FaceRecognition from "./Components/FaceRecognition/FaceRecognition";
import ParticlesBg from 'particles-bg';
import React, { useState } from 'react'; // Importing useState hook

function App() {
  // State to manage the input image URL, image recognition results, and route
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState(""); // State to store the image URL for recognition
  const [box, setBox] = useState([]); // State to store bounding box for face detection
  const [route, setRoute] = useState("Signin"); // State to manage the current route
  const [isSignedIn, setIsSignedIn] = useState(false); // Default value is 'false'
  const [user, setUser] = useState({      
    id: "",
    name: "",
    email: "",
    entries: 0,
    joined: ""
  });

  const loadUser = (data) => {
    setUser({
      id: data.id,
      name: data.name,
      email: data.email,
      entries: data.entries,
      joined: data.joined
    });
  };

  // Event handler for input change
  const onInputChange = (event) => {
    setInput(event.target.value); // Update state (if needed)
  };

  // Function to calculate the bounding box
  const displayFaceBox = (face) => {
    setBox(prevBoxes => [...prevBoxes, face]); // Append new boxes to the array
  };

  const onButtonSubmit = () => {
    console.log("Input URL:", input);
    setImageUrl(input); // Update the imageUrl state

    // Clarifai API call via proxy
    fetch("http://localhost:3000/clarifai-proxy", {  // Call your backend endpoint
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ imageUrl: input }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Clarifai API error: ${response.status}`);
        }
        return response.json(); // Parse the Clarifai response
      })
      .then(data => {
        if (data.outputs && data.outputs[0].data.regions) {
          const clarifaiFaces = data.outputs[0].data.regions.map(region => {
            return region.region_info.bounding_box;
          });

          setBox([]);  // Clear the box array before adding new boxes
          clarifaiFaces.forEach((face) => {
            displayFaceBox(face); // Display bounding boxes
          });

          // Update user rank on the server
          return fetch("http://localhost:3000/image", {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              id: user.id, // Assuming `user` is accessible in this scope
            }),
          });
        } else {
          console.log("No faces detected in the image.");
        }
      })
      .then(response => {
        if (response && !response.ok) {
          throw new Error(`Server error: ${response.status}`);
        }
        return response ? response.json() : null; // Handle case where there's no server response
      })
      .then(count => {
        if (count) {
          // Update user's entries
          setUser(prevUser => ({
            ...prevUser,
            entries: count.entries, // Adjust based on your server response structure
          }));
          console.log("Rank updated successfully:", count);
        }
      })
      .catch(err => console.error("Error:", err)); // Catch and log errors
  };

  // Function to change the route (e.g., from Signin to Home)
  const changeRoute = (route) => {
    console.log("Changing route:", route);  // Debugging line
    if (route === "signout") {
      setIsSignedIn(false);
      setRoute("Signin");
    } else if (route === "home") {
      setIsSignedIn(true);
    }
    setRoute(route); // Update the route state
  };

  return (
    <div className="App">
      <ParticlesBg className="particles"
        type="cobweb"
        color="#ffffff"
        num={300}
        bg={true}
      />
      <Navigation isSignedIn={isSignedIn} changeRoute={changeRoute} />
      {route === "home"  
        ? <div>
            <Logo />
            <Rank name={user.name} entries={user.entries} />
            <ImageLinkForm 
              onInputChange={onInputChange} 
              onButtonSubmit={onButtonSubmit} 
            />
            <FaceRecognition imageUrl={imageUrl} box={box} />
          </div>
          : (
            route === "Signin"
            ? <Signin changeRoute={changeRoute} />
            : <Register loadUser={loadUser} changeRoute={changeRoute} />
            )
      }
    </div>
  );
}

export default App;
